import React, { useState, useEffect, useRef } from 'react';

// Aseg√∫rate de que la Web Speech API est√© disponible y maneja prefijos de navegador
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

const SpeechToTextPTT = () => {
  const [recognizedText, setRecognizedText] = useState(''); // Estado para almacenar el texto reconocido
  const [isListening, setIsListening] = useState(false); // Estado para indicar si est√° escuchando
  const [error, setError] = useState(''); // Estado para posibles errores

  // Usamos useRef para mantener una referencia a la instancia de SpeechRecognition
  // y a un flag para saber si la tecla Enter est√° siendo mantenida presionada
  const recognitionRef = useRef(null);
  const isHoldingEnter = useRef(false);

  // Este useEffect se encarga de configurar el API de reconocimiento de voz y los listeners de eventos
  useEffect(() => {
    // Si la API no est√° disponible, mostramos un error y salimos
    if (!SpeechRecognition) {
      setError("La Web Speech API no es soportada por este navegador.");
      console.error("La Web Speech API no es soportada por este navegador.");
      return;
    }

    // --- Configuraci√≥n del Reconocimiento de Voz ---
    const recognition = new SpeechRecognition();
    recognition.continuous = false; // Queremos resultados al soltar la tecla, no reconocimiento continuo
    recognition.interimResults = false; // Solo queremos resultados finales, no intermedios
    recognition.lang = 'es-ES'; // Configura el idioma (c√°mbialo si necesitas otro)

    // --- Eventos del Reconocimiento de Voz ---
    recognition.onstart = () => {
      // Este evento se dispara cuando el micr√≥fono comienza a escuchar
      setIsListening(true);
      setError(''); // Limpiar cualquier error previo al iniciar
      console.log('Reconocimiento de voz iniciado.');
    };

    recognition.onresult = (event) => {
      // Este evento se dispara cuando se reconoce una porci√≥n de voz
      const transcript = event.results[event.results.length - 1][0].transcript;
      console.log('Resultado parcial o final:', transcript);
      // Aqu√≠, como interimResults es false, este ser√° el resultado final de la pausa de habla o al detenerse
      setRecognizedText(prevText => (prevText ? prevText + ' ' : '') + transcript); // A√±adir el nuevo texto reconocido
    };

    recognition.onerror = (event) => {
      // Este evento se dispara si ocurre un error
      console.error('Error en reconocimiento de voz:', event.error);
      setIsListening(false); // Asegurar que el estado de escucha se desactive en caso de error
      // Dependiendo del error, podr√≠as querer mostrar un mensaje diferente
      let errorMessage = `Error de reconocimiento: ${event.error}`;
       if (event.error === 'not-allowed') {
            errorMessage = 'Se deneg√≥ el permiso del micr√≥fono.';
       } else if (event.error === 'no-speech') {
            errorMessage = 'No se detect√≥ voz.';
       } else if (event.error === 'network') {
            errorMessage = 'Error de red.';
       }
       setError(errorMessage);
       isHoldingEnter.current = false; // Reset flag

    };

    recognition.onend = () => {
      // Este evento se dispara cuando el reconocimiento termina (se llama a stop() o hay un error/no-speech)
      console.log('Reconocimiento de voz finalizado.');
      setIsListening(false); // Asegurar que el estado de escucha se desactive
      isHoldingEnter.current = false; // Asegurar que el flag se resetee

    };

    // Guardar la instancia en la referencia para poder acceder a ella en otros handlers
    recognitionRef.current = recognition;

    // --- Handlers de Eventos del Teclado ---
    const handleKeyDown = (event) => {
      // Solo actuamos si es la tecla Enter y no la estamos manteniendo ya presionada
      if (event.key === 'Enter' && !isHoldingEnter.current) {
        isHoldingEnter.current = true; // Marcar que Enter est√° presionada
        // No llamamos a start() aqu√≠ directamente, lo haremos de forma controlada
        // a veces es mejor dejar que onstart actualice el estado, pero aqu√≠ lo hacemos para feedback r√°pido
        setIsListening(true);
        setError(''); // Limpiar error al intentar hablar

        try {
           // Abortar cualquier sesi√≥n previa que pudiera estar pendiente
           if(recognitionRef.current) {
               recognitionRef.current.abort(); // Abortar detiene inmediatamente y dispara 'onend'
           }
           // Iniciar una nueva sesi√≥n de reconocimiento
           recognitionRef.current.start();
        } catch (e) {
            // Esto puede pasar si intentas start() mientras ya est√° iniciando o activa (raro con el abort() previo)
            console.error("Error al intentar iniciar el reconocimiento:", e);
            setIsListening(false);
            isHoldingEnter.current = false;
            setError("No se pudo iniciar el micr√≥fono.");
        }

      }
    };

    const handleKeyUp = (event) => {
      // Solo actuamos si es la tecla Enter y la est√°bamos manteniendo presionada
      if (event.key === 'Enter' && isHoldingEnter.current) {
        isHoldingEnter.current = false; // Marcar que Enter ha sido soltada
        // Llamar a stop() para detener el reconocimiento. Esto disparar√° 'onend'.
        if (recognitionRef.current && isListening) { // Solo detener si estaba escuchando
             recognitionRef.current.stop();
        } else {
            // Si solt√≥ Enter pero no estaba escuchando (ej: error justo al presionar),
            // solo asegurarnos de que el flag y estado est√©n correctos.
             setIsListening(false); //redundante con onend, pero seguro
        }
      }
    };

    // A√±adir los listeners de eventos al objeto global 'window'
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // --- Funci√≥n de Limpieza (se ejecuta al desmontar el componente) ---
    return () => {
      console.log('Limpiando componente SpeechToTextPTT.');
      // Remover los listeners de eventos para evitar fugas de memoria
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);

      // Abortar el reconocimiento si todav√≠a est√° activo al desmontar el componente
      if (recognitionRef.current && (recognitionRef.current.recognizing || isListening)) {
        recognitionRef.current.abort(); // abort() es m√°s seguro para limpieza r√°pida
      }
      recognitionRef.current = null; // Limpiar la referencia
    };

  }, []); // El array vac√≠o [] asegura que este efecto se ejecute solo una vez al montar el componente

  // --- Renderizado del Componente ---
  return (
    <div style={{ margin: '20px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', fontFamily: 'sans-serif' }}>
      <h2>Control por Voz (Enter para Hablar)</h2>

      <p style={{ marginBottom: '15px', fontSize: '1.1em' }}>
        Estado: {isListening ?
                 <span style={{ color: 'green', fontWeight: 'bold' }}>üéôÔ∏è Escuchando...</span> :
                 <span style={{ color: 'gray' }}>üîò Presiona y mant√©n la tecla Enter para hablar</span>
               }
      </p>

      {error && (
          <p style={{ color: 'red', fontWeight: 'bold' }}>Error: {error}</p>
      )}

      <div style={{ marginTop: '15px', padding: '15px', border: '1px dashed #bbb', minHeight: '80px', backgroundColor: '#f9f9f9', borderRadius: '5px', wordBreak: 'break-word' }}>
        <strong>Texto Reconocido:</strong>
        <p>{recognizedText || 'El texto de la voz aparecer√° aqu√≠...'}</p>
      </div>

      {recognizedText && ( // Mostrar el bot√≥n de limpiar solo si hay texto
          <button
            onClick={() => { setRecognizedText(''); setError(''); }} // Limpiar texto y errores
            style={{ marginTop: '15px', padding: '8px 15px', cursor: 'pointer', border: '1px solid #ccc', borderRadius: '5px', backgroundColor: '#eee' }}
          >
            Limpiar Texto
          </button>
      )}
    </div>
  );
};

export default SpeechToTextPTT;
